export default function Form({ todos, setTodos, setInputText }) {
  const inputTextHandler = (e) => {
      setInputText(e.target.value);
   };

   const submitTodoHandler = (e) => {
       e.preventDefault();
       setTodos([...todos, description])
   }

  return (<>
     <form>
         <input type="text" placeholder="Add something to do..." onChange={inputTextHandler}></input> 
         <button type="submit" onClick={submitTodoHandler}>Add</button>
     </form>
  </>);
}
