import React, { useState, useEffect } from "react";
import todosData from "./todosData";
import ToDoItem from "./ToDoItem";

export default function ToDoList() {
  const [dataObject, setDataObject] = useState([...todosData]); 
  useEffect(() => {
    fetch("./api/data").then(
        res => setState(res.data)
    )
}, [])
  const toDoItems = dataObject.map((toDoItem) => <ToDoItem key={toDoItem.id} toDoItem={toDoItem} />);

  return (
    <>
      <ul>{toDoItems}</ul>
    </>
  );
}
