import React from "react";

export default function Form({ todos, setTodos, inputText, setInputText }) {
  const inputTextHandler = (e) => {
    setInputText(e.target.value);
  };

  const submitTodoHandler = (e) => {
    e.preventDefault();
    setTodos([...todos, { id: Math.random() * 1000, description: {inputText}, completed: false }]);
    setInputText()
  };

  return (
    <>
      <form>
        <input type='text' placeholder='Add something to do...' onChange={inputTextHandler}></input>
        <button type='submit' onClick={submitTodoHandler}>
          Add
        </button>
      </form>
    </>
  );
}
